import React, { Component } from 'react'


export class EditFish extends Component {
    render() {
        return (
            <div className="fish-edit">
                <input type="text" name="name" placeholder="name"   onChange={handleChange} value={this.props.fish.name}/>
                <input type="text" name="price"  placeholder="price"  onChange={handleChange} value={this.props.fish.price}/>
                <select type="text" name="status" placeholder="status"  onChange={handleChange} value={this.props.fish.states} >
                    <option value="available">Fresh!</option>
                    <option value="unavailable">sold Out!</option>
                </select>
                <textarea name="desc"  placeholder="desc"  onChange={handleChange} value={this.props.fish.desc} />
                <input type="text" name="image"  placeholder="image"  onChange={handleChange} value={this.props.fish.image} /> 

            </div>
        )
    }
}

export default EditFish
