import React, { Component } from 'react'
import { formatPrice } from '../helpers'
import { TransitionGroup, CSSTransition } from "react-transition-group"


export class Order extends Component {


    renderOrder = (key) => {
        const fish = this.props.fishes[key];
        const count = this.props.order[key];
        const isAvailable = fish && fish.status === 'available';
        if(!fish) return null;

        if(isAvailable) {
        return (<CSSTransition 
                    classNames="order" 
                    key={key} 
                    timeout={{enter:500, exit:50}}
                    >

                    <li key={key} index={key} >
                        <span> 
                            <TransitionGroup component="span" className="count">
                                <CSSTransition 
                                classNames="count" 
                                key={count}
                                timeout={{enter:500, exit:500}}
                                >  
                                    <span>{count}</span> 
                                </CSSTransition > 
                            </TransitionGroup>
                            lbs {fish.name}
                            {formatPrice(count * fish.price)}
                            <button onClick={() => this.props.deleteOrder(key)}>
                                &times;
                            </button>
                        </span>
                    </li>

                </CSSTransition>
                );

        } else {
        return <li key={key}>sorry {fish ? fish.name : 'fish'} is not available</li>
        }
    }

    render() {
        const orderIds = Object.keys(this.props.order);
        const total = formatPrice(orderIds.reduce((prevTotal, key) => {
            const fish = this.props.fishes[key];
            const count = this.props.order[key];
            const isAvailable = fish && fish.status === 'available';
            if(isAvailable) {
                return prevTotal+ (count * fish.price);
            } else {
                return prevTotal;
            };
        }, 0));

        return (
            <div className="order-wrap">
            <h2>Order</h2>
            <TransitionGroup component="ul" className="order">
            {orderIds.map(this.renderOrder)}
            </TransitionGroup>

            <div className="total">
                Total:
                <strong>{total}</strong>
            </div>
            </div>
        ) 
    } 
}

export default Order
